    private void btnPesquisarSorvetesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPesquisarSorvetesActionPerformed
        try {
            // Pega o sabor digitado no jTextField
            String sabor = txtSaborSorvete.getText().trim();

            // Buscando os sorvetes pelo sabor
            SorvetesDao dao = new SorvetesDao();
            ArrayList<Sorvetes> lista = dao.listarSorvetesPorSabor(sabor);

            // Atualiza a tabela com os resultados encontrados
            DefaultTableModel modelo = (DefaultTableModel) tblListaSorvetes.getModel();
            modelo.setRowCount(0);

            for (Sorvetes s : lista) {
                modelo.addRow(new Object[]{
                    s.getId(),
                    s.getSaborSorvete(),
                    s.getCategoria(),
                    s.getPreco(),
                    s.getUnidade(),
                    s.getQuantidade(),
                    s.getFornecedor().getNome(),
                    s.getDataEntrada(),
                    s.getDataValidade()
                });
            }

        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Erro ao pesquisar sorvete: " + e.getMessage());
            e.printStackTrace();
        }
    }//GEN-LAST:event_btnPesquisarSorvetesActionPerformed

