    public void listarTodosSorvetes() {
        try {
            SorvetesDao dao = new SorvetesDao();
            ArrayList<Sorvetes> lista = dao.listarSorvetes();

            DefaultTableModel modelo = (DefaultTableModel) tblListaSorvetes.getModel();
            modelo.setRowCount(0);

            for (Sorvetes s : lista) {
                modelo.addRow(new Object[]{
                    s.getId(),
                    s.getSaborSorvete(),
                    s.getCategoria(),
                    s.getPreco(),
                    s.getUnidade(),
                    s.getQuantidade(),
                    s.getFornecedor().getNome(),
                    s.getDataEntrada(),
                    s.getDataValidade()
                });
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Erro ao listar sorvetes: " + e.getMessage());
        }
    }

