    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
        try {
            if (txtNome.getText().trim().isEmpty() || txtCnpj.getText().trim().isEmpty()) {
                JOptionPane.showMessageDialog(null, "Nome e CNPJ são obrigatórios!");
                return;
            }

            Fornecedores fornecedor = new Fornecedores();

            fornecedor.setNome(txtNome.getText());
            fornecedor.setCnpj(txtCnpj.getText());
            fornecedor.setEmail(txtEmail.getText());
            fornecedor.setCelular(txtCelular.getText());
            fornecedor.setCep(txtCep.getText());
            fornecedor.setEndereco(txtEndereco.getText());
            fornecedor.setNumero(txtNumero.getText());
            fornecedor.setBairro(txtBairro.getText());
            fornecedor.setCidade(txtCidade.getText());
            fornecedor.setComplemento(txtComplemento.getText());
            fornecedor.setUnidade(cmbxUf.getSelectedItem().toString());

            FornecedoresDao fornecedoresDao = new FornecedoresDao();
            fornecedoresDao.cadastroFornecedores(fornecedor);

            listarTodosFornecedores();

            JOptionPane.showMessageDialog(null, "Fornecedor cadastrado com sucesso!");

        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Não foi possível cadastrar o fornecedor: " + e.getMessage());
            e.printStackTrace();
        }

    }//GEN-LAST:event_btnCadastrarActionPerformed

    private void btnAtualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAtualizarActionPerformed
        try {
            if (txtNome.getText().trim().isEmpty() || txtCnpj.getText().trim().isEmpty()) {
                JOptionPane.showMessageDialog(null, "Nome e CNPJ são obrigatórios!");
                return;
            }

            Fornecedores fornecedor = new Fornecedores();

            fornecedor.setId(Integer.parseInt(txtId.getText()));
            fornecedor.setNome(txtNome.getText());
            fornecedor.setCnpj(txtCnpj.getText());
            fornecedor.setEmail(txtEmail.getText());
            fornecedor.setCelular(txtCelular.getText());
            fornecedor.setCep(txtCep.getText());
            fornecedor.setEndereco(txtEndereco.getText());
            fornecedor.setNumero(txtNumero.getText());
            fornecedor.setBairro(txtBairro.getText());
            fornecedor.setCidade(txtCidade.getText());
            fornecedor.setComplemento(txtComplemento.getText());
            fornecedor.setUnidade(cmbxUf.getSelectedItem().toString());

            FornecedoresDao fornecedoresDao = new FornecedoresDao();
            fornecedoresDao.atualizarFornecedores(fornecedor);

            listarTodosFornecedores();

            JOptionPane.showMessageDialog(null, "Fornecedor atualizado com sucesso!");

        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Não foi possível atualizar o fornecedor: " + e.getMessage());
            e.printStackTrace();
        }

    }//GEN-LAST:event_btnAtualizarActionPerformed

    private void btnPesquisarNomeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPesquisarNomeActionPerformed
        try {
            String nome = txtNome.getText().trim();

            if (nome.isEmpty()) {
                JOptionPane.showMessageDialog(null, "Digite o nome do fornecedor para pesquisar.");
                return;
            }

            FornecedoresDao dao = new FornecedoresDao();
            Fornecedores fornecedor = dao.buscarNomeFornecedor(nome);

            if (fornecedor != null) {
                txtId.setText(String.valueOf(fornecedor.getId()));
                txtNome.setText(fornecedor.getNome());
                txtCnpj.setText(fornecedor.getCnpj());
                txtEmail.setText(fornecedor.getEmail());
                txtCelular.setText(fornecedor.getCelular());
                txtCep.setText(fornecedor.getCep());
                txtEndereco.setText(fornecedor.getEndereco());
                txtNumero.setText(fornecedor.getNumero());
                txtBairro.setText(fornecedor.getBairro());
                txtCidade.setText(fornecedor.getCidade());
                txtComplemento.setText(fornecedor.getComplemento());
                cmbxUf.setSelectedItem(fornecedor.getUnidade());

                JOptionPane.showMessageDialog(null, "Fornecedor encontrado!");
            } else {
                JOptionPane.showMessageDialog(null, "Fornecedor não encontrado!");
            }

        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Erro ao buscar fornecedor: " + e.getMessage());
            e.printStackTrace();
        }

    }//GEN-LAST:event_btnPesquisarNomeActionPerformed

    private void btnExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExcluirActionPerformed
        try {
            if (txtId.getText().trim().isEmpty()) {
                JOptionPane.showMessageDialog(null, "Pesquise um fornecedor antes de excluir.");
                return;
            }

            int confirmacao = JOptionPane.showConfirmDialog(
                    null,
                    "Tem certeza que deseja excluir este fornecedor?",
                    "Confirmação",
                    JOptionPane.YES_NO_OPTION
            );

            if (confirmacao == JOptionPane.YES_OPTION) {
                Fornecedores fornecedor = new Fornecedores();
                fornecedor.setId(Integer.parseInt(txtId.getText())); // este id foi o id preenchido na pesquisa

                FornecedoresDao dao = new FornecedoresDao();
                dao.excluirFornecedores(fornecedor);

                listarTodosFornecedores();

                JOptionPane.showMessageDialog(null, "Fornecedor excluído com sucesso!");
            }

        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Erro ao excluir fornecedor: " + e.getMessage());
            e.printStackTrace();
        }
    }//GEN-LAST:event_btnExcluirActionPerformed

