    private void btnPesquisarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPesquisarActionPerformed
        try {
            // Pega o nome digitado no jTextField
            String nome = txtNomeFornecedor.getText().trim();

            // Buscando o fornecedor pelo nome
            FornecedoresDao dao = new FornecedoresDao();
            ArrayList<Fornecedores> lista = dao.listarFornecedoresPorNome(nome);

            // Atualiza a tabela com os resultados encontrados
            DefaultTableModel modelo = (DefaultTableModel) tblDadosFornecedores.getModel();
            modelo.setRowCount(0);

            for (Fornecedores f : lista) {
                modelo.addRow(new Object[]{
                    f.getId(),
                    f.getNome(),
                    f.getEmail(),
                    f.getCnpj(),
                    f.getCep(),
                    f.getEndereco(),
                    f.getNumero(),
                    f.getBairro(),
                    f.getCidade(),
                    f.getUnidade(),
                    f.getCelular()
                });
            }

        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Erro ao pesquisar fornecedor: " + e.getMessage());
            e.printStackTrace();
        }
    }//GEN-LAST:event_btnPesquisarActionPerformed

