    public void listarTodosFornecedores() {
        try {
            FornecedoresDao dao = new FornecedoresDao();
            ArrayList<Fornecedores> lista = dao.listarFornecedores();

            DefaultTableModel modelo = (DefaultTableModel) tblDadosFornecedores.getModel();
            modelo.setRowCount(0);

            for (Fornecedores f : lista) {
                modelo.addRow(new Object[]{
                    f.getId(),
                    f.getNome(),
                    f.getEmail(),
                    f.getCnpj(),
                    f.getCep(),
                    f.getEndereco(),
                    f.getNumero(),
                    f.getBairro(),
                    f.getCidade(),
                    f.getUnidade(),
                    f.getCelular()
                });
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Erro ao listar fornecedores: " + e.getMessage());
        }
    }

